import React from "react";
import style from "./Recipe.module.css";
const Recipe = (props) => {
  const [hide,setHide]=React.useState("");
  const [visible,setVisible]=React.useState("");
  const [showIngredients,setshowIngredients]=React.useState();
  const [hideIngredients,sethideIngredients]=React.useState(style.hideIngredients);

  const showRecipe=()=>{
    console.log(props.ingredientsArray);
    setHide(style.hide);
    setshowIngredients(style.showIngredients);
    sethideIngredients();

  }
  return (
    <div className={style.recipe}>
      <div className={`${style.imagediv} ${hide}`}>
        <img className={style.foodimg} src={props.image} alt=""></img>
        <button onClick={showRecipe}className={style.btn}>See Recipe</button>
      </div>
      <div className={`${style.info} ${hide}`}>
        <h1>{props.title}</h1>
      </div>
      <div className={`${style.ingredients} ${hideIngredients} ${showIngredients} `}>
        <ul>
            {props.ingredientsArray.map((ingredient)=><li>{ingredient}</li>)}



        </ul>
        <button></button>
      </div>
    </div>
  );
};
export default Recipe;
