import React,{useEffect,useState} from "react";
import style from "./App.module.css";
import Recipe from "./components/Recipe";

const App = () => {
  const APP_ID = "8e7c0c48";
  const APP_KEY = "a2be49acfa8d7bb350167b1b348b6f79";
  const search_term = "burger";
  const [search,setSearch]=useState("Burger");
  const [query,setQuery]=useState("");
  const [recipes,setRecipes]=useState([]);

  const url =
    "https://api.edamam.com/api/recipes/v2?type=public&q=" +
    search_term +
    "&app_id=" +
    APP_ID +
    "&app_key=" +
    APP_KEY;
    const getResponse = async () => {
        const response = await fetch(url);
        const data = await response.json();
        setRecipes(data.hits);

      };


    useEffect(()=>{
        getResponse();


    },[query]);


const submitHandler=(e)=>{

e.preventDefault();
alert("Hello!")
setQuery(search)

}
const inputChangeHandler=(e)=>{
console.log(e.target.value);
const searched=e.target.value;
setSearch(searched);

}
  return (
    <div>
      <form onSubmit={submitHandler}className={style.form}>

            <input placeholder="So,what's cooking?"className={style.searchbar} onChange={inputChangeHandler}></input>
            <button className={style.searchbtn} type="submit">Search recipes</button>


      </form>
      <div className={style.recipesbox}>
      {recipes.map(recipe=>

      <Recipe title={recipe.recipe.label} calories={recipe.recipe.calories} image={recipe.recipe.image}/>



      )}
      </div>

    </div>
  );
};
export default App;
