import React, { useEffect, useState } from "react";
import style from "./App.module.css";
import Recipe from "./components/Recipe";

const App = () => {
  const APP_ID = "8e7c0c48";
  const APP_KEY = "a2be49acfa8d7bb350167b1b348b6f79";
  //   const search_term = "burger";
  const [search, setSearch] = useState("Burger");
  const [query, setQuery] = useState("Pasta");
  const [recipes, setRecipes] = useState([]);
  const [image, setImage] = useState("https://cdn.cssauthor.com/wp-content/uploads/2018/06/Copper-Loader.gif?strip=all&lossy=1&resize=400%2C300&ssl=1");


  const url =
    "https://api.edamam.com/api/recipes/v2?type=public&q=" +
    query +
    "&app_id=" +
    APP_ID +
    "&app_key=" +
    APP_KEY;

  useEffect(() => {
    getResponse();

  }, [query]);

  const getResponse = async () => {
    setImage("https://cutewallpaper.org/21/loading-gif-transparent-background/Pin-on-UI-UX.gif");

    const response = await fetch(url);
    const data = await response.json();
    setRecipes(data.hits);
    setImage("");
    if(data.hits.length===0){
        setImage("https://o.remove.bg/downloads/a4294aa3-8e55-4993-bdfe-4b88ff847d65/png-removebg-preview.png");
    }
  };

  const submitHandler = (e) => {
    e.preventDefault();

    setQuery(search);
  };
  const inputChangeHandler = (e) => {
    console.log(e.target.value);
    const searched = e.target.value;
    setSearch(searched);
  };
  return (
    <div>
      <form onSubmit={submitHandler} className={style.form}>
        <input
          placeholder="Pasta"
          className={style.searchbar}
          onChange={inputChangeHandler}
        ></input>
        <button className={style.searchbtn} type="submit">
          Search recipes
        </button>
      </form>
      <div className={style.recipesbox}>
        {recipes.map((recipe) => 


              <Recipe
                title={recipe.recipe.label}
                calories={recipe.recipe.calories}
                image={recipe.recipe.image}
              />

        )  
        }
        <img src={image} alt=""></img>
      </div>
    </div>
  );
};
export default App;
